import React, { useState } from "react";
import { ModalCustom } from "./styled";
import { CloseBut } from "modules/ModalMember/styled";
import ButtonMember from "modules/ButtonMember";
import { LinkCustom } from "modules/FooterMember/styled";

const PositionTab = () => {
  const [modalShow, setModalShow] = useState(false);
  const [nameState, setnameState] = useState('กรุณาเลือกตำแหน่ง')
  const OnClickforChange = (namePage) => {
    setnameState(namePage)
  }

  const MyVerticallyCenteredModal = (props) => {
    return (
      <ModalCustom
        closeButton
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <CloseBut closeButton>
          <div>
            <div>
              <LinkCustom to="/MemberLibrary/President">President</LinkCustom>
            </div>
            <div>
              <LinkCustom to="/MemberLibrary/Vice President">
                Vice President
              </LinkCustom>
            </div>
            <div onClick={OnClickforChange("Secretary General")}>Secretary General</div>
            <div>Deputy Secretary General</div>
            <div>Assistant Secretary General</div>
            <div>Senior Executive Director</div>
            <div>Chief Executive Director</div>
            <div>Deputy Chief Executive Director</div>
          </div>
        </CloseBut>
      </ModalCustom>
    );
  };

  return (
    <div>
      <div onClick={() => setModalShow(true)}>
        <ButtonMember name={nameState} />
      </div>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </div>
  );
};

export default PositionTab;
