import React from "react";
import {
  Card,
  PicBox,
  BodyBox,
  Topic,
  RedLine,
  BlackLine,
  UnderLineBox,
} from "./styled";
import test from "modules/images/test.jpg";
import { useHistory } from "react-router-dom";
import {dangerouslySetInnerHTML} from 'react-dom'

// const discribeTry = (discribe) => {
//   ReactDOM.render(
//     <React.StrictMode>
//       <BodyBox>{discribe}</BodyBox>
//     </React.StrictMode>,
//     document.getElementById("root")
//   );
// };

const NewsCard = ({ nameTopic, discribe, pic }) => {
  const history = useHistory();
  const OnClickDetailNews = () => {
    history.push({ pathname: "/DetailNews" });
  };

  return (
    <Card onClick={OnClickDetailNews}>
      <PicBox src={pic} />
      <Topic>
        <div>{nameTopic}</div>
        <UnderLineBox>
          <RedLine />
          <BlackLine />
        </UnderLineBox>
      </Topic>
      {/* {discribeTry} */}
      <BodyBox dangerouslySetInnerHTML>{discribe}</BodyBox>
    </Card>
  );
};

export default NewsCard;
